function zczy_msg(msg, callback) {
	if ($("#alerttishiyu").attr("id") != "alerttishiyu") {
		$("body").append("<div id=\"alerttishiyu\"><div>");
		$("#alerttishiyu").css({
			"border" : "3px solid #43BE0E",
			"width" : "200px",
			"color" : "red",
			"text-align" : "center",
			"padding" : "15px",
			"font-size" : "20px",
			"background-color" : "white",
			"word-break" : "break-all"
		});
		$("#alerttishiyu").zczy_window({
			mask : false,
			donghua : true
		});
	}
	$("#alerttishiyu").html(msg);
	$("#alerttishiyu").zczy_window("open");
	setTimeout(function() {
		$("#alerttishiyu").zczy_window("close");
		if (callback) {
			callback.call();
		}
	}, 2000);
}

function zczy_alert(msg,callback) {
	
	if ($("#zczy_alert").attr("id") != "zczy_alert") {
		$("body")
				.append(
						"<div id=\"zczy_alert\" style=\"display:none\" class=\"tuich_tcbox\"><div class=\"tuich_tit\"><a class=\"guanbi\" href=\"javascript:;\">关闭</a>温馨提示</div><dl class=\"tuich_dl\"><dd><p>&nbsp;</p><span class=\"confirm_msg\" style='word-break: break-all'></span><p>&nbsp;</p></dd><dt><a class=\"tuich_btn queding\" href=\"javascript:;\">确定</a></dt></dl></div>");
		$("#zczy_alert").zczy_window();
	}
	
	
	$("#zczy_alert .confirm_msg").html(msg);
	$("#zczy_alert .queding").click(function(){
		$("#zczy_alert").zczy_window("close");
		if(typeof callback =="function"){
			callback.call();
		}
	});
	$("#zczy_alert .guanbi").click(function(){
		$("#zczy_alert").zczy_window("close");
	});
	$("#zczy_alert").zczy_window("open");
}

function loading(e){
	if($("#loadingimg").attr("id")!="loadingimg"){
		var $loading = $("<img id='loadingimg' src='"+getRootPath()+"/Public/common/zczy/images/loading.gif' />");
		$("body").append($loading);
		$loading.zczy_window();
	}
	
	
	if(e=="close"){
		$("#loadingimg").zczy_window("close");
	}else{
		$("#loadingimg").zczy_window("open");
	}
}
