/**
 * 获取页面宽高度
 */
function getPageSize() {
	var xScroll, yScroll;
	if (window.innerHeight && window.scrollMaxY) {
		xScroll = document.body.scrollWidth;
		yScroll = window.innerHeight + window.scrollMaxY;
	} else if (document.body.scrollHeight > document.body.offsetHeight) {
		xScroll = document.body.scrollWidth;
		yScroll = document.body.scrollHeight;
	} else {
		xScroll = document.body.offsetWidth;
		yScroll = document.body.offsetHeight;
	}
	var windowWidth, windowHeight;
	if (self.innerHeight) {
		windowWidth = self.innerWidth;
		windowHeight = self.innerHeight;
	} else if (document.documentElement
			&& document.documentElement.clientHeight) {
		windowWidth = document.documentElement.clientWidth;
		windowHeight = document.documentElement.clientHeight;
	} else if (document.body) {
		windowWidth = document.body.clientWidth;
		windowHeight = document.body.clientHeight;
	}
	if (yScroll < windowHeight) {
		pageHeight = windowHeight;
	} else {
		pageHeight = yScroll;
	}
	if (xScroll < windowWidth) {
		pageWidth = windowWidth;
	} else {
		pageWidth = xScroll;
	}
	arrayPageSize = new Array(pageWidth, pageHeight, windowWidth, windowHeight,
			xScroll, yScroll)
	return arrayPageSize;
}
function addEvent(obj, evtType, func, cap) {
	cap = cap || false;
	if (obj.addEventListener) {
		obj.addEventListener(evtType, func, cap);
		return true;
	} else if (obj.attachEvent) {
		if (cap) {
			obj.setCapture();
			return true;
		} else {
			return obj.attachEvent("on" + evtType, func);
		}
	} else {
		return false;
	}
}
function getPageScroll() {
	var xScroll, yScroll;
	if (self.pageXOffset) {
		xScroll = self.pageXOffset;
	} else if (document.documentElement && document.documentElement.scrollLeft) {
		xScroll = document.documentElement.scrollLeft;
	} else if (document.body) {
		xScroll = document.body.scrollLeft;
	}
	if (self.pageYOffset) {
		yScroll = self.pageYOffset;
	} else if (document.documentElement && document.documentElement.scrollTop) {
		yScroll = document.documentElement.scrollTop;
	} else if (document.body) {
		yScroll = document.body.scrollTop;
	}
	arrayPageScroll = new Array(xScroll, yScroll);
	return arrayPageScroll;
}
/**
 * 会员等级
 * 
 * 使用方法 
 * 	<div id="abc" name="name" pingjia="1" all="4"></div>
	$("#abc").zczy_pj();
 */
(function($) {
	var zczy_pj_sets = new Object();
	var zczy_pj_imgs = [ "xing_hui.png", "xing_huang.png"];/*等级图标*/
	var methods = {
		init : function(options, picjsonarray) {
			return this.each(function() {
				var $this = $(this);
				var zczy_id = $this.attr("zczy_id");
				if (zczy_id) {
					return;
				}
				var uuid = getuuid();
				$this.attr("zczy_id", uuid);
				zczy_pj_sets[uuid] = $this;
				var pingjia = $this.attr("pingjia");/*分数，默认为0*/
				if(isNaN(pingjia)){
					pingjia=0;
				}else{
					pingjia = pingjia/1;	
				}
				var name = $this.attr("name");/*分数，默认为0*/
				$this.append("<input type=\"hidden\" value=\""+pingjia+"\" name=\""+name+"\"/>");
				var all = $this.attr("all");/*总分数，默认为5*/
				if(isNaN(all)){
					 $this.attr("all","5")
				}
				methods.fresh.apply($this);
			});
			
		},
		fresh : function(options, picjsonarray) {
			return this.each(function() {
				var $this = $(this);
				var zczy_id = $this.attr("zczy_id");
				if (!zczy_id) {
					return;
				}
				
				var input = $this.find("input").eq(0);
				var pingjia = input.val()/1;
				var all = $this.attr("all")/1;
				
				
				
				
				$this.find("img").remove();
				var index = 1;
				for(var i=0;i<pingjia;i++){
					$this.append("<img index=\""+index+++"\" src=\""+getRootPath+"/Public/common/zczy_images/xing_huang.png\" />");
				}
				for(var i=0;i<all-pingjia;i++){
					$this.append("<img index=\""+index+++"\" src=\""+getRootPath+"/Public/common/zczy_images/xing_hui.png\" />");	
				}
			
				$this.find("img").click(function(){
					$this.find("input").val($(this).attr("index"));
					methods.fresh.apply($this);
				});
			});
			}
	};

	$.fn.zczy_pj = function() {
		var method = arguments[0];

		if (methods[method]) {
			method = methods[method];
			arguments = Array.prototype.slice.call(arguments, 1);
		} else if (typeof (method) == 'object' || !method) {
			method = methods.init;
		} else {
			$
					.error('Method ' + method
							+ ' does not exist on jQuery.pluginName');
			return this;
		}
		return method.apply(this, arguments);
	}
})(jQuery);
/**
 * 会员等级
 * 
 * 使用方法 
 * 	<div id="abc" jifen="100000"></div>
	$("#abc").zczy_hydj();
 */
(function($) {
	var zczy_hydj_sets = new Object();
	var zczy_hydj_imgs = [ "s_red_1.png", "s_blue_1.png", "s_crown_1.png",
			"s_cap_1.png" ];/*等级图标*/
	var zczy_hydj_dj=[4,11,41,91,151,251,501,1001,2001,5001,10001,20001,50001,100001,200001,500001,1000001,2000001,5000001,10000001];/*积分等级*/
	var methods = {
		init : function(options, picjsonarray) {
			return this.each(function() {
				var $this = $(this);
				var zczy_id = $this.attr("zczy_id");
				if (zczy_id) {
					return;
				}
				var uuid = getuuid();
				$this.attr("zczy_id", uuid);
				zczy_hydj_sets[uuid] = $this;
				var jifen = $this.attr("jifen");
				if(isNaN(jifen)){
					alert("信用度为非数字");
					return;
				}
				if(jifen<4){
					$this.append("0");
					return;
				}
				var jibie = 0;/*级别*/
				for(var i=0;i<zczy_hydj_dj.length-1;i++){
					if(zczy_hydj_dj[i]<=jifen&&jifen<zczy_hydj_dj[i+1]){
						jibie=i+1;
						break;
					}
				}
				if(jibie==0){
					jibie=20;
				}
				var imgindex=Math.floor(jibie/5);/*使用第几张图片（zczy_hydj_imgs下标）*/
				var imgnum=jibie%5;
				if(jibie%5==0){
					imgindex--;
					imgnum=5;
				}
				
				var img = zczy_hydj_imgs[imgindex];/*要使用的图标*/
				$this.html("");
				for(var i=0;i<imgnum;i++){
					$this.append("<img src=\""+getRootPath+"/Public/common/huiyuandengji/"+img+"\" />");
				}
				
			});
		}
	};

	$.fn.zczy_hydj = function() {
		var method = arguments[0];

		if (methods[method]) {
			method = methods[method];
			arguments = Array.prototype.slice.call(arguments, 1);
		} else if (typeof (method) == 'object' || !method) {
			method = methods.init;
		} else {
			$
					.error('Method ' + method
							+ ' does not exist on jQuery.pluginName');
			return this;
		}
		return method.apply(this, arguments);
	}
})(jQuery);
$(function(){
	var b = $.browser;
	/*b.msie && b.version < 10*/
	if (true) {
			$("*[tishiyu]").each(function() {
				var 	$this = $(this);
				if($this.attr("type")=="password"){
					$this.css("display","none");
					$this.after("<input type1=\"password\" class=\""+$this.attr("class")+"\" value=\""+$this.attr("tishiyu")+"\"/>");
					$this.next().focus(function(){
						$(this).css("display","none");
						$this.css("display","");
						$this.focus();
					});
				}else{
					if($this.val()==""){
						$this.attr("value", $this.attr("tishiyu"));
					}
				}
				
				$this.focus(function(){
					
					if($this.attr("type1")=="password"){
						$this.show();
						$this.next().hide();
						return;
					}
					
					if($this.val()==$this.attr("tishiyu")){
						$this.val("");
					}
				});
				$this.blur(function(){
					if($this.val()==""){
						if($this.attr("type")=="password"){
							$this.hide();
							$this.next().show();
						}else{
							$this.attr("value", $this.attr("tishiyu"));
						}
					}
				});
			});
	}else{
		$("*[tishiyu]").each(function(){
			$(this).attr("placeholder",$(this).attr("tishiyu"));
			
		});
		
	}
	
});

